// number of iterations (you can change)
var iterations;
background(255, 0, 0);
var drawTriangle = function(iterations, x1, y1, x2, y2, x3, y3) {
    triangle(x1, y1, x2, y2, x3, y3);
    if (iterations > 0) {
        var f = 2;
        var x12 = (x1 + x2) / f;
        var x13 = (x1 + x3) / f;
        var x23 = (x2 + x3) / f;
        var y12 = (y1 + y2) / f;
        var y13 = (y1 + y3) / f;
        var y23 = (y2 + y3) / f;
        //changes the iteration each time
        iterations--;
        //colors the triangle randomly
        //draws the original triangles
        drawTriangle(iterations, x1, y1, x12, y12, x13, y13);
        stroke(x12 % 200, x13 % 200, x23 % 200);
        drawTriangle(iterations, x2, y2, x12, y12, x23, y23);
        stroke(x12 % 200, x13 % 200, x23 % 200);
        drawTriangle(iterations, x3, y3, x13, y13, x23, y23);
        stroke(x12 % 200, x13 % 200, x23 % 200);
    }
};
stroke(random(0,255), random(0,255), random(0,255));
strokeWeight(2);
var draw = function() {
        frameRate(100);
        if(frameCount % 4000 < 400){
            background(255, 0, 0);
            textSize(400/18);
            textAlign(CENTER, CENTER);
            text("Serpinsky triangles", width/2, height/2);
        }
        if(frameCount % 4000 === 400){
            iterations = 1; 
            drawTriangle(iterations, width/2, 10, 10, height-10, width-10, height-10);
        }
        if(frameCount % 4000 === 800){
            iterations = 2; 
