/***
 * AUTHOR : ThinkInvisible
 * DATE   : 6/10/2015
 * PROGRAM: Crystallizer
 * VERSION: 1.1
 * DESCRIPTION:
    Entry for the Generative Art contest.
    Generates a bunch of unevenly distributed beveled rectangles, simulating a light source from the top left corner in a way that makes the rectangles look crystalline.
 * ABOUT ME:
    I'm currently waiting for my second year as an electrical engineering major at a small Wisconsin college. I've always been interested in programming because a computer can do so many different things -- and something in my brain really enjoys chasing bugs around as they leave ten times as many in their wake! I've spent a lot of time writing little Javascript and C# programs just to prove a concept or to make something neat to look at for a few minutes.
 * OTHER ENTRIES:
    Branches          - https://www.khanacademy.org/computer-programming/generative-art-branches/6353011494486016
    Cellular Automata - https://www.khanacademy.org/computer-programming/generative-art-cellular-automata/5085736565080064
 * SOURCES:
    Based on my BSP library, originally at https://www.khanacademy.org/computer-programming/binary-space-partitioning/6569935257468928
 * CHANGELOG:
    ####### 1.0 [ 6/10/2015]
        Initial version.
    ####### 1.1 [ 6/11/2015]
        Adjusted slow mode.
        Fixed slow rendering caused by drawing the entire tree every frame.
    ####### 1.2 [ 6/12/2015]
        Cleaned and commented.
 * ISSUES:
    Output is slightly unpredictable; the program may need to be reset a few times to get a good result.
***/

//PROGRAM OPTIONS//
var basesize = 2;       //In general, controls the size of the drawing. Must be greater than or equal to 1; recommended values are 2 through 4.
var csmodifier = 3.5;   //Affects how drastic color changes are. Recommended value is 3 for basesize of 3 or 4, and 3.5 for basesize of 2.
var slowmode = true;    //If false, all leaves will be split every frame. If true, exactly [slowmospeed] leaves will be split every frame. Recommended value is true.
var slowmospeed = 250;  //Number of squares to create every frame if slowmode is enabled. Ideal value depends on your computer speed.
var autoreset = true;   //If true, another wall will be drawn 10 seconds after the previous wall has completed.
var arDebug = true;     //If true, a popup will be displayed showing how many splits and iterations the current wall took and a countdown to the next wall.

//VARIABLES//
var colorScale = basesize/csmodifier;   //Adjusts csmodifier based on size so the color range stays more stable over all values of basesize.
var gen_MinSize = basesize;             //Leaves below this size cannot be split.
var gen_MaxSize = basesize * 4;         //Leaves above this size must be split.
var gen_WHBias = 0.29;                   //The maximum aspect ratio and maximum inverse aspect ratio of a leaf, after which the program will force it to split along its short axis.
var gen_SplitBias = 0.11;               //Chance to avoid randomly splitting a leaf if it isn't larger than maxSize.

//SUPPORT FUNCTIONS//
//Returns a random integer between min and max, inclusive.
var randInt = function(min, max) {
    return floor(random() * (max - min + 1)) + min;
};

//Creates a new Leaf with a certain position and size and no children.
var Leaf = function(position, size) {
    this.position = position;
    this.size = size;
    this.leftChild = null;
    this.rightChild = null;
};

//Applies a function to a leaf, the leaf's children, the leaf's childrens' children,  etc. in the reverse of that order.
Leaf.prototype.applyFunctionDepthFirst = function(func) {
    if(this.leftChild !== null) {
        this.leftChild.applyFunctionDepthFirst(func);
    }
    if(this.rightChild !== null) {
        this.rightChild.applyFunctionDepthFirst(func);
    }
    func.call(this);
};

//Applies a function to a leaf, the leaf's children, the leaf's childrens' children,  etc. in that order.
Leaf.prototype.applyFunctionBreadthFirst = function(func) {
    func.call(this);
    if(this.leftChild !== null) {
        this.leftChild.applyFunctionBreadthFirst(func);
    }
    if(this.rightChild !== null) {
        this.rightChild.applyFunctionBreadthFirst(func);
    }
};

//Generates a leaf's children based on system variables. Returns true on success and false on failure.
Leaf.prototype.Split = function() {
    if(this.leftChild !== null || this.rightChild !== null) {return false;} //This leaf already has children; we can't create more!

    //Choose a direction to split in; determine whether to override this if the aspect ratio is too high or too low, or if splitting in the randomly chosen direction would make the leaf too small.
    var splitH = random() > 0.5;
    var max = (splitH ? this.size.y : this.size.x) - gen_MinSize;
    if(max <= gen_MinSize) {splitH = !splitH;}
    if(this.size.x > this.size.y && this.size.y / this.size.x >= gen_WHBias) {splitH = false;}
    if(this.size.y > this.size.x && this.size.x / this.size.y >= gen_WHBias) {splitH = true;}

    //If this split would still cause the leaf to fall below the minimum size, abort.
    max = (splitH ? this.size.y : this.size.x) - gen_MinSize;
    if(max <= gen_MinSize) {return false;}

    //Choose a position along the chosen axis to split; position/size children based on this line and the position/size of the current leaf.
    var splitN = randInt(gen_MinSize, max);
    if(splitH) {
        this.leftChild = new Leaf(
            this.position,
            new PVector(this.size.x, splitN)
        );
        this.rightChild = new Leaf(
            new PVector(this.position.x, this.position.y + splitN),
            new PVector(this.size.x, this.size.y - splitN)
        );
    } else {
        this.leftChild = new Leaf(
            this.position,
            new PVector(splitN, this.size.y)
        );
        this.rightChild = new Leaf(
            new PVector(this.position.x + splitN, this.position.y),
            new PVector(this.size.x - splitN, this.size.y)
        );
    }

    //Keep track of depth of recursion.
    this.leftChild.iteration = this.iteration + 1;
    this.rightChild.iteration = this.iteration + 1;
    this.leftChild.scrollscore = this.scrollscore - 1;
    this.rightChild.scrollscore = this.scrollscore + 1;
    return true;
};

//MAIN PROGRAM//
colorMode(HSB);
var drawLeaf = function() {
    fill((this.iteration*10*colorScale)%255,constrain(this.scrollscore*25*colorScale+125,0,255),255);
    rect(this.position.x, this.position.y, this.size.x, this.size.y);
};

var tree;                //Stores the current tree.
var gen_Iterations = 0;  //Number of iterations since the current tree was created.
var gen_Splits = 0;      //Number of times the current tree has been split successfully.
var gen_Running = false; //True if the generation loop is expected to be running.
var gen_Active = false;  //True while the generation loop is actually running.
var LastGen = millis();  //Stores the last time that generation ended.

//Handles variable updates for genLoop.
var genFinalize = function() {
    gen_Running = false;
    LastGen = millis();
};
//Here's the main program itself!
var genLoop = function(numTicks) {
    var remTicks = numTicks; //Buffer for number of ticks remaining.
    gen_Active = true; //Set the activity flag so nothing else messes with our data or tries to start another loop.
    gen_Iterations++;

    var didSplit = false;
    tree.applyFunctionDepthFirst(function() {
       if(didSplit && slowmode && remTicks === 0){ //If we're out of iterations, give control back to whatever called us.
           return;
       }
       //Handles the actual split operation as well as rendering.
       if(this.size.x > gen_MaxSize || this.size.y > gen_MaxSize || random() > gen_SplitBias) {
           if(this.Split(gen_MinSize)) {
               drawLeaf.call(this.leftChild);
               drawLeaf.call(this.rightChild);
               didSplit = true;
               gen_Splits ++;
               remTicks --; //Iterations are actually only drained on successful splits, not failed ones; be careful with extremely small minimum sizes/extremely large initial sizes!
           }
       }
    });

    //Finish up; don't tell the program we're done generating if there are still splits possible.
    gen_Active = false;
    if(!didSplit){
        genFinalize();
    }
};

//Creates the initial tree.
var genInit = function() {
    tree = new Leaf(new PVector(0, 0), new PVector(width, height));
    tree.iteration = 0;
    tree.scrollscore = 0;
    gen_Iterations = 0;
    gen_Splits = 0;
    gen_Running = true;
};

//Handles the start/stop logic; PJS boilerplate.
var draw = function() {
    if(gen_Running && !gen_Active) {
        noStroke();
        genLoop(slowmospeed);
    } else if(!gen_Running && autoreset) {
        if(millis() - LastGen > 10000) {
            background(0,0,0);
            genInit();
        } else if(arDebug) {
            stroke(0, 255, 255);
            fill(0, 0, 45);
            rect(3, 3, width - 6, 16);
            fill(255);
            text("finished with " + gen_Splits + " splits over " + gen_Iterations + " iteration(s)    restarting in " + ceil(10 - (millis() - LastGen)/1000), 4, 14);
        }
    }
};

//Engage!
genInit();
