var Box = function(x,y,w,h,d) {
/*
The center of the screen with width & height of 400 is x:200,y:200. To make calculations easier this program moves the origin to that point. Now, when x,y coordinates 0,0 are used, then it refers to the center of screen. The z axis starts with 0 being about 500 pixels "into" the screen with negative values being farther and positive values are closer.

With perspective, the apparent height is inversely proportional to its distance away from the viewer. A yardstick is 36 inches long no matter where it is placed, but if you move it twice the distance away from you, then its length only looks half the original length. Move it halfway closer to you, then its length looks twice the original.

If you have an object that is 200 pixels away and you move it closer, then it may look too big while farther objects look too small. In these cases you want to adjust your point of view by moving farther away from the object while simultaneously zooming in. You probably have seen this in movies for effect when the person in the foreground stays the same size, while a hallway seems to stretch. What happens is that the camera gets closer to the person while the focus is pulled out to keep the person from appearing larger.

This program changes the size of rectangles to simulate relative movement and perspecitve. The pixels are still lit on the surface of the monitor, while the size of the rectangles change to make the plane tilt and turn. The cos function is used  to make the center of the squares bubble up and move outward. Physically, nothing is moving, it is all just an illusion.

*/

    // center position relative to screen
    // is at 200,200

    // x and y coordinates range from 
    // -200 to 200
    this.x = x;
    this.y = y;
    // original
    this.xo = x;
    this.yo = y;
    // alternate
    this.xa = x;
    this.ya = y;
    // z starts farther from screen at 0
    // closest objects are at 200
    // while farthers object are at -200
    this.z = 0;
    // width of box
    this.w = w;
    // height of box
    this.h = h;
    // depth of the box
    this.d = d;
    // field length increases distance
    // and zooms in to reduce distortion
    // make this small to exaggerate the change in
    // distance and larger to flatten it
    this.f = 400;
    // distance from center to point
    this.dxy = dist(this.x,this.y,0,0);
    // outside value
    this.v = 0;

    this.move = function(a,i) {
        // the z value is the position along
        // the z axis where 0 lies on the xy plane
        // with negative being farther away
        // and positive being closer

        // this expression calculates two z values
        // and uses lerp to gradually transition
        // between them two
        var a = this.dxy+frameCount;
        this.xa = this.xo+this.dxy*cos(a)*this.v;
        this.ya = this.yo+this.dxy*sin(a)*this.v;
        this.x = lerp(this.xo,this.xa,this.v);
        this.y = lerp(this.yo,this.ya,this.v);
        this.z = lerp(200+sin(frameCount*3-dist(this.x,this.y,0,0))*200,this.x*sin(millis()/17)+this.y*sin(millis()/18),sin(millis()/17)/2+0.5);
        // remove the comments from this one
        // to use the Perlin noise function
        //this.z = 800*noise(this.x/100,this.y/100,frameCount/400)-400;
        this.dxyz = sqrt(sq(this.x)+sq(this.y)+sq(this.f-this.z));
    };

    this.transform = function() {
        // scaled front
        // this is the apparent change in size
        // for xy coordinates at value z
        var sf = this.f/(this.f+200-this.z);
        // scaled x value
        var sfx = this.x*sf;
        // scaled y value
        var sfy = this.y*sf;
        // scaled width divided by two 
        // to make calculations faster
        var sfw = this.w*sf/2;
        // scaled height
        var sfh = this.h*sf/2;
        // scaled back
        var sb = this.f/(this.f+200-this.z+this.d);
        var sbx = this.x*sb;
        var sby = this.y*sb;
        var sbw = this.w*sb/2;
        var sbh = this.h*sb/2;
        // this array holds the back and front
        // coordinates for each vertex of the
        // box
        // first index is 0:back, 1:front
        // second index is 0:top-left 1:top-right
        // 2:bottom-left 3:bottom-right
        // third index is 0:x coordinate 1:y coordinate
        this.m = [[[sbx-sbw,sby-sbh],[sbx+sbw,sby-sbh],[sbx-sbw,sby+sbh],[sbx+sbw,sby+sbh]],[[sfx-sfw,sfy-sfh],[sfx+sfw,sfy-sfh],[sfx-sfw,sfy+sfh],[sfx+sfw,sfy+sfh]]];
    };

    this.draw = function(a,i) {
        // move is called separately so the
        // objects can be sorted by z value first

        // calls the function to compute the 
        // point as it appears on the monitor
        this.transform();
        noStroke();
        // for faster rendering, 
        // comment from here to ....
        /*fill(this.x,this.y,60);
        // rear
        rect(this.m[0][0][0],this.m[0][0][1],this.m[0][3][0],this.m[0][3][1]);
        /*
        // left
        quad(this.m[0][0][0],this.m[0][0][1],this.m[0][2][0],this.m[0][2][1],this.m[1][2][0],this.m[1][2][1],this.m[1][0][0],this.m[1][0][1]);
        // top
        quad(this.m[0][0][0],this.m[0][0][1],this.m[0][1][0],this.m[0][1][1],this.m[1][1][0],this.m[1][1][1],this.m[1][0][0],this.m[1][0][1]);
        // right
        quad(this.m[0][1][0],this.m[0][1][1],this.m[0][3][0],this.m[0][3][1],this.m[1][3][0],this.m[1][3][1],this.m[1][1][0],this.m[1][1][1]);
        // bottom
        quad(this.m[0][2][0],this.m[0][2][1],this.m[0][3][0],this.m[0][3][1],this.m[1][3][0],this.m[1][3][1],this.m[1][2][0],this.m[1][2][1]);
        // ..to here.
        */

        // front
        // color is based on the xyz values
        // frameCount makes the blue channel
        // rotate
        fill(abs(this.x),abs(this.y),(this.z+frameCount*3)%256);
        // draws rectangle from top-left corner
        // to bottom-right
        rect(this.m[1][0][0],this.m[1][0][1],this.m[1][3][0],this.m[1][3][1]);
    };
};

// draw rectangles by specifying both corners
ellipseMode(ROUND);

// array holds all Box objects
var b = [];
// create boxes spaced apart
var bl = 20;
// box width height
var bwh = bl*3/4;
for(var y=-200; y<200; y+=bl){
    for(var x=-200; x<200; x+=bl){
        // add a new box to the array 
        // with width and length
        // and depth
        b.push( new Box( x,y, bwh,bwh,60 ) );
    }
}
draw = function() {
    // reset matrix values
    resetMatrix();
    // fill with transparent black
    fill(0x88000000);
    // draw transparent rectangle over screen
    rect(0,0,width,height);
    // move xy the origin to screen center
    translate(200,200);
    // slowly rotate the image
    // around the origin
    rotate(frameCount/4);
    // calculate v for all objects
    var v = sin(frameCount)/2+0.5;
    // loop through all objects to
    // calculate the new position
    for(var i=0; i<b.length; i++){
        b[i].v = v;
        b[i].move(b,i);
    }
    // sort the array by xy distance
    // from center from highest to lowest
    // middle objects are sorted last
    // since they will overlap objects farther
    // away
    b.sort(function(a,b){
        return a.dxy<b.dxy;
    });
    // loop through all objects and 
    // draw them a their new virtual position
    for(var i=0; i<b.length; i++){
        b[i].draw(b,i);
    }
};
