/*
    A sun!

    Give it time to load. Once the counter reaches 360,
    the animation will speed up.

    There's a special event every 6 minutes!

    See the original at:
    * https://www.khanacademy.org/computer-programming/animated-sun/6526591131451392
*/

// Larger steps makes it load faster, but makes the animation less smooth.
var step = 5;
//The size of the sun.
var toScale = 2;

var noiseVal;
var noiseScale = 0.06;
translate(width / 2, height / 2);
scale(toScale);
imageMode(CENTER);

var lon0 = 0;
var lat0 = 0;

var R = 100 * PI / TWO_PI;
var s = 1.6;

var color1 = [225, 128, 10];
var color2 = [225, 192, 10];

var planet = [];

var draw = function() {
    background(0);
    if(planet.length < 360 / step) {
        var cosLat0 = Math.cos(lat0);
        var sinLat0 = Math.sin(lat0);
        var atm = 25;
        for(var i = 0; i < atm; i+=1) {
            var _h = (atm - i);
            noStroke();
            var _p = atm / _h / 10;
            fill(_p * color1[0], _p * color1[1], _p * color1[2], 100);
            ellipse(0.5, 0.5, 64 + _h, 64 + _h);
        }

        for(var y = -50; y < 50; y++) {
            var lat = map(y, -50, 50, -HALF_PI, HALF_PI);
            var cosLat = Math.cos(lat);
            var sinLat = Math.sin(lat);
            for(var x = -50; x < 50; x+=0.5) {
                var lon = map(x, -50, 50, -PI, PI) - lon0;
                var cosLon = Math.cos(lon);
                var sinLon = Math.sin(lon);

                noiseDetail(5.5, 0.70);
                noiseVal = noise(x * noiseScale*0.7, y * noiseScale);

                var _x = R * Math.cos(lat) * Math.sin(lon);
                var _y = R * cosLat0 * sinLat - sinLat0 * cosLat * cosLon;
                var _z = cosLon;
                var r = Math.sqrt(_x*_x + _y*_y);
                var c = Math.asin(r / R);

                if(_z >= 0 && c >= 0) {
                    var col1 = color(noiseVal * color2[0], noiseVal * color2[1], noiseVal * color2[2]);
                    stroke(col1);
                    var frostLat = radians(35);/*
                    if(abs(lat) > frostLat) {
                        stroke(lerpColor(col1, color(255),
                               constrain(
                                   (abs(lat) - frostLat) / (0.5*(PI - frostLat)),
                                   0, 0.5)
                               )
                        );
                    }*/
                    point(_x/s, _y/s);
                }
            }
        }
        lon0 += radians(step);
        filter(BLUR);
        planet.push(get(width / 2 - 50*toScale, height / 2 - 50*toScale, 100*toScale, 100*toScale));
        text(planet.length * step, 150/toScale, 150/toScale);
    } else {
        var im = planet[floor(millis() / (20*step)) % planet.length];
        image(im, 0, 0, im.width / toScale, im.height / toScale);
    }

    fill(0);
    noStroke();

    if(sin(millis() / 1000) > 0) {
        ellipse(cos(millis() / 1000) * 100, 0, 33 * toScale, 33 * toScale);
    }
};
