// NOTE: When grading, please make sure you experiment with ALL six variables, as there's more to this program than the default settings.
// If you can't do so, I have some spin-offs pre-loaded with interesting values.
/** Please write my name in as "T#1B12P" in the winners list if I win.
 * Age/grade: [not telling]
 * Programming experience: I don't know really, like 6-8 years?
 * Location: USA, Eastern time zone. That's all that I'm saying.
 * 
 * This entry may not look too interesting, until you zoom out to see the bigger picture. Every third square is red, but every third red square is changed to green, but then every third GREEN square is BLUE, and so on.
 * 
 * Noticed that on 400x400, base 3 there are diagonal stripes. Cool.
 * 
 * WARNING: Staring at a low-size drawing (or searching for a specific color in one) may be harmful to the eye.
 * 
 * You can also make it show the number. 
 */

var BASE = 3;
var FRAME_RATE = 30;
var LOOPS = 30; // number of times per draw() the program adds a new thingie
var SIZE = 10; // size of text/squares
var useText = false; // true = text, false = squares
var outlines = true; // adds outlines to the squares (ignored if SIZE <= 6)

// Do not change.
var N = 1;
// Calculates the number of zeros in the expansion of n (returns zero if n === 0)
var calc = function(n) {
    var c = 0;
    if (n <= 0) {return 0;}

    while (n % BASE === 0) {
        c++;
        n /= BASE;
    }
    return c;
};

var x = 0;
var y = 0;

var _RangeError = RangeError || function(msg) {
    // KA doesn't like RangeError
    this.message = msg;
    this.toString = function() {
        return "RangeError: " + this.message;
    };
};

if (BASE !== round(BASE) || BASE < 2 || BASE > 36) {
    throw "Error: Invalid base " + BASE + ". Must be an integer between 2 and 36.";
}
if (SIZE < 1 || SIZE > min(width, height)) {
    throw new _RangeError("SIZE must be greater than 1 and less than the size of the canvas");
}

var colors = [null,
    color(15, 15, 15), // 1
    color(240, 15, 15), // 3
    color(15, 240, 15), // 9
    color(15, 15, 240), // 27 or 23
    color(15, 240, 240), // 81 or 69
    color(240, 15, 240), // 243 or 183
    color(240, 240, 15), // 729 or 509
    color(240, 240, 240), // 2187 or 1323
    color(221, 221, 160), // 6561 or 3969
    color(124, 207, 135), // 19683 or E483
    color(85, 85, 255), // 59049 or 2X209
    color(153, 153, 153), // 177147 or 217669
    color(231, 237, 112), // 531441 or 64X783
];
/*for (var _ = 1; _ < colors.length; _++) {
    println(pow(3, _) + " in decimal or " + pow(3, _).toString(12) + " in dozenal");
}*/

if (SIZE <= 6 || !outlines) {noStroke();} // we can't afford to waste space with outlines
else {stroke(0, 0, 0);}

if (SIZE < 6 && useText) {
    throw "You can't have the size less than 6 on text mode, it's too hard to see and strains the eye.";
}

frameRate(min(FRAME_RATE, 40));

var runOnce = false;
var draw = function() {
    if (!runOnce) {
        image(getImage("space/background"), 0, 0, width, height);
        runOnce = true;
    }
    for (var _ = 0; _ < LOOPS; _++) {
        // Step 1: Calculate!
        var tile = calc(N) + 1;
        fill(colors[tile]);

        // Step 2: Draw!
        if (useText) {
            // WIP
            textAlign(CENTER, CENTER);
            textSize(SIZE * 0.95);
            text(tile, x + SIZE/2, y + SIZE/2);
        } else {
            rect(x, y, SIZE, SIZE);
        }
        // Step 3: Move and check for stopping!
        N++;
        x += SIZE;
        if (x >= width) {
            y += SIZE;
            x = 0;
        }
        if (y >= height) {
            //println("Got to " + N + " iteration(s)");
            noLoop();
            break;
        }
    }
};
