var Design3 = function(){

    noStroke();
    fill(Red,Green,Blue,80+20*cos(h));
    beginShape();
    vertex(0,200+200*cos(h));
    bezierVertex(sin(h*4)*100,cos(h*5)*100,sin(h*9)*100,cos(h*7)*100,400,200+200*sin(h));
    bezierVertex(cos(h*4)*78,sin(h*5)*100,cos(h*9)*100,sin(h*7)*100,0,200+200*cos(h));
    vertex(400,200+200*sin(h));
    endShape();
    // If any color gets too large or small, this makes it change direction.
    if (Red > 255) {
        Red_Change = -1;
    }
    if (Red < 0) {
        Red_Change = 1;
    }
    if (Green > 255) {
        Green_Change = -2;
    }
    if (Green < 0) {
        Green_Change = 2;
    }
    if (Blue > 255) {
        Blue_Change = -2.5;
    }
    if (Blue < 0) {
        Blue_Change = 2.5;
    }

    //Here's where the changes happen.
    Red =Red + Red_Change;
    Green = Green + Green_Change;
    Blue = Blue + Blue_Change;

};
