noStroke();

//scene 1
var timer = -1;
var moveGear = -75;
var moveGear2 = 0;
var rotateGear = -1;
var placeGear = 80;
var timer2 = 0;
var timer3 = 0;
var timer4 = 0;
var timer5 = 0;
var timer6 = 250;
var backImage = function(t,T) {
    timer4 ++;
    if (timer4 > 50) {
        this.bubble = random(47 - t/7,37);
        this.bubble2 = random(5,240);
        this.bubblea = random(47 - t/7,37);
        this.bubble2a = random(5,240);
        this.bubbleb = random(47 - t/7,37);
        this.bubble2b = random(5,240);
        this.bubblec = random(47 - t/7,37);
        this.bubble2c = random(5,240);
        this.bubbled = random(47 - t/7,37);
        this.bubble2d = random(5,240);
        timer4 = 0;
    }
    background(50,50,50);
    stroke(200,200,200);
    strokeWeight(10);
    fill(150,150,150);
    ellipse(0,0,200,200);
    noStroke();
    triangle(125,0,125,50,100,50);
    triangle(0,125,50,125,50,100);
    quad(50,100,100,50,125,50,50,125);
    stroke(75,75,75);
    strokeWeight(5);
    fill(100,100,100);
    rect(2,127,45,270);
    rect(127,2,270,45);
    noStroke();
    fill(51, 255, 0);
    rect(15,height - t - 15,20,t,3);
    fill(72, 122, 26);
    rect(138,height - t/7 - 357,250,t/7,3);
    if (t > 50) {
        stroke(200,200,200);
        strokeWeight(1);
        fill(0, 255, 30);
        ellipse(this.bubble2 + 138,this.bubble,10,10);
        ellipse(this.bubble2a + 138,this.bubblea,10,10);
        ellipse(this.bubble2b + 138,this.bubbleb,10,10);
        ellipse(this.bubble2c + 138,this.bubblec,10,10);
        ellipse(this.bubble2d + 138,this.bubbled,10,10);
        noStroke();
    }

    for (var i = 140; i < 385; i += 10) {
        stroke(0);
        strokeWeight(2);
        line(16,i,20,i);
        noStroke();
    }
    for (var i = 10; i < 45; i += 10) {
        stroke(0);
        strokeWeight(2);
        line(140,i,145,i);
        noStroke();
    }

    fill(225,225,225);
    stroke(125,125,125);
    strokeWeight(5);
    rect(100,100,200,250);
    noStroke();

    fill(81, 102, 67);
    triangle(200,300,275,200,125,200);
    quad(275,200,125,200,150,150,250,150);

    fill(57, 117, 42);
    rect(105,height-T - 45,190,T - 10);

    fill(175,175,175);
    rect(150,50,50,48);
    for (var i = 54; i < 97; i += 5) {
        stroke(0);
        strokeWeight(2);
        line(151,i,198,i);
    }
};
var gear = function() {
    noStroke();
    fill(150,150,150);
    rect(-25,-100,50,25);
    rect(-25,75,50,20);
    rect(75,-25,25,50);
    rect(-100,-25,50,50);
    pushMatrix();
    rotate(45);
    fill(150,150,150);
    rect(-25,-100,50,25);
    rect(-25,75,50,20);
    rect(75,-25,25,50);
    rect(-100,-25,50,50);
    popMatrix();
    strokeWeight(15);
    stroke(175,175,175);
    ellipse(0,0,150,150);
    noStroke();
    fill(175,175,175);
    ellipse(0,0,25,25);
    noStroke();
};
var gear2 = function() {
    noStroke();
    stroke(150,150,150);
    strokeWeight(5);
    fill(100,100,100);
    rect(0,-10,48,1200);
    noStroke();
    strokeWeight(1);
    stroke(200,200,200);
    fill(175,175,175);
    for(var i = -10;i < 1200;i += 60){
        triangle(-2,i,-2,i+ 50,-25,i+ 25);
    }
    noStroke();
    fill(200,200,200);
    rect(-350,490,400,10);
    fill(255,255,255);
    rect(-350,500,400,400);
    noStroke();
};
var textJCA = function() {
    noStroke();
    fill(0);
    rect(0,0,400,50);
    rect(0,350,400,50);
    rect(0,0,50,400);
    rect(350,0,50,400);
    rect(50,125,50,175);
    triangle(125,350,175,350,150,325);
    triangle(250,350,275,350,250,325);
    triangle(350,350,325,350,350,325);
    rect(275,200,50,150);
    rect(275,100,50,50);
    rect(150,125,100,25);
    triangle(150,150,175,150,150,175);
    rect(200,200,50,100);
    rect(200,50,50,75);
    rect(247.5,0,5,400);
    rect(147.5,125,5,275);
};
var W = random(10,50);
var W2 = random(10,60);
var W3 = random(10,20);
var H = 400;
var lava = function() {

};
lava.prototype.circ = function(x,y,w,h) {
    fill(random(220,255), random(65,125), random(20,50));
    ellipse(x,y,w,h);
};
lava.prototype.add = function(w,w2,h) {

    for (var i = -10; i < 500; i += W3) {
        var w2 = random(10,50);
        lava.prototype.circ(i,h,w2,w2);
    }

};
lava.prototype.draw = function() {
    for (var h = 400; h > H; h -= 10) {
        lava.prototype.add(W,W2,h);
    }
};
var scene1 = function(){
    if (timer5 < 250) {
        timer5 ++;
    }
    if (timer6 > 20) {
        timer6 --;
    }
    backImage(timer5, timer6);
    if (moveGear2 > -500) {
    timer ++;
    rotateGear = -timer;
    if (placeGear > -25) {
        placeGear --;
    }
    }
    if (timer > 101) {
        if(moveGear2 > -500) {
            moveGear2 --;
        }
    }

    pushMatrix();
    rotate(placeGear);
    stroke(50,50,50);
    strokeWeight(25);
    line(0,0,185,180);
    noStroke();
    translate(185,180);
    rotate(rotateGear);
    gear();
    popMatrix();
    pushMatrix();
    translate(350,moveGear2);
    gear2();
    popMatrix();
};
var scene2 = function() {
    background(255,255,255);
    if (timer2 < 800) {
    timer2 ++;
    }
    fill(0,0,0,timer2);
    rect(0,0,400,400);
    if (timer2 > 275) {
        fill(62, 138, 52);
        rect(0,0,400,timer2 - 275);
    }
    if (timer2 > 250) {
        textJCA();
    }
    if (timer2 > 700) {
        fill(0,0,0,(timer2 - 700) * 2);
        rect(0,0,400,400);
    }
};
var Timer = 0;
var a = 0, b = 0, c = 0, d = 0,t = 0;
var a1 = false,b1 = false,c1 = false,d1 = false,s2bDone = false;
var scene2b = function() {
    this.done = false;
    if (Timer < 400) {
        Timer += 5;
    } else if (Timer >= 400){
        if (a1 === false) {
            a1 = true;
            Timer = 0;
        } else {
            if (b1 === false) {
                b1 = true;
                Timer = 0;
            } else {
                if (c1 === false) {
                    c1 = true;
                    Timer = 0;
                } else {
                    if (d1 === false) {
                        d1 = true;
                        Timer = 0;
                    } else {
                        s2bDone = true;
                    }
                }
            }
        }
    }
    if (a1) {a += 5;}
    if (b1) {b += 5;}
    if (c1) {c += 5;}
    if (d1) {d += 5;}
    noStroke();
    fill(255,255,255);
    rect(0,0,a,100);
    rect(0,100,b,100);
    rect(0,200,c,100);
    rect(0,300,d,100);
};
var scene3 = function() {
    if (H > -100) {H --;}
    lava.prototype.draw();
    if (H <= -100) {
        if (timer3 < 300) {
        timer3 ++;
        }
        fill(100,100,100,timer3);
        textFont(createFont("impact"),50);
        text("JCA",158,200);
        text("uniVerse",110,260);
    }
};
var endTimer = 0;
var draw = function() {
    if (moveGear2 > -500) {
        scene1();
    } else if (timer2 < 800 && moveGear2 <= -500) {
        scene2();
    } else if (timer2 >= 800 && s2bDone === false){
        scene2b();
    }
    if (s2bDone && endTimer < 800) {scene3(); endTimer ++;} else if (endTimer > 800) {fill(0,0,0);rect(0,0,400,400);}
};
