var drawBackground = function() {
    noStroke();
    fill(0, 225, 255);
    rect(0, 0, 400, 400);
    fill(12, 199, 24);
    bezier(400, 400, 114, 149, -106, 237, 0, 400);
    bezier(400, 400, 569, 241, -18, 191, -258, 400);
    fill(21, 117, 2);
    bezier(400, 400, 327, 355, -100, 200, 0, 400);
    fill(12, 199, 24);
    bezier(400, 400, 219, 417, 110, 263, 0, 400);
    fill(255, 255, 255);
    ellipse(115, 35, 55, 58);
    ellipse(147, 35, 55, 58);
    ellipse(245, 60, 111, 23);
    ellipse(102, 84, 28, 33);
    ellipse(100, 114, 28, 33);
    ellipse(84, 103, 28, 33);
    ellipse(119, 104, 28, 33);
    ellipse(342, 27, 27, 47);
    ellipse(321, 31, 27, 47);
};

var drawError = function(x, y) {
    pushMatrix();
    translate(x, y);
    //Error tab
    stroke(0, 43, 255);
    strokeWeight(5);
    fill(255, 255, 255);
    rect(158, 206, 120, 60, 5);
    strokeWeight(1);
    fill(0, 43, 255);
    rect(158, 209, 120, 15, 5);
    stroke(255, 0, 0);
    fill(255, 0, 0);
    ellipse(180, 243, 35, 35);
    fill(255, 255, 255);
    textSize(26);
    text("X", 172, 254);
    fill(0, 0, 0);
    textSize(25);
    text("Error!", 205, 255);
    popMatrix();
};

var drawCaution = function(x, y) {
    pushMatrix();
    translate(x, y);
    stroke(0, 43, 255);
    strokeWeight(5);
    fill(255, 255, 255);
    rect(158, 206, 120, 60, 5);
    strokeWeight(1);
    fill(0, 43, 255);
    rect(158, 209, 120, 15, 5);
    stroke(255, 242, 0);
    fill(255, 242, 0);
    triangle(180, 225, 165, 255, 195, 255);
    fill(0, 0, 0);
    textSize(26);
    text("!", 177, 254);
    fill(0, 0, 0);
    textSize(23);
    text("Careful!", 195, 252);
    popMatrix();
};

var drawBlueScreen = function() {
    background(0, 51, 255);
    fill(255, 255, 255);
    textSize(20);
    text("Error detected! Not working!\n  Operating System Error...", 5, 30);
};

angleMode = "radians";

var angle = 0;

var errorSize = 20;

var xIsSinMode = true;

var yIsSinMode = false;

var visible = true;

var blueScreenTime = 0;

drawBackground();

draw = function() {
    strokeWeight(1);

    var xOffset = random(-100, 100);
    var yOffset = random(-100, 100);
    var xOffset2 = random(-100, 100);
    var yOffset2 = random(-100, 100);

    //X and Y variables(credits for Jessica Liu)
    var x = sin(angle) * errorSize + xOffset;
    var y = cos(angle) * errorSize + yOffset;
    var x2 = sin(angle) * errorSize + xOffset2;
    var y2 = cos(angle) * errorSize + yOffset2;

    //To make a new angle!
    if(angle > 120) {
        angle = 0;
        errorSize = 20;
        xIsSinMode = false;
    }

    //either clockwise or counterClockwise
    if(xIsSinMode === true) {
        x = sin(angle) * errorSize + xOffset;
        y = cos(angle) * errorSize + yOffset;
        x2 = sin(angle) * errorSize + xOffset2;
        y2 = cos(angle) * errorSize + yOffset2;
    }
    if(yIsSinMode === true) {
        x = cos(angle) * errorSize + xOffset;
        y = sin(angle) * errorSize + yOffset;
        x2 = cos(angle) * errorSize + xOffset2;
        y2 = sin(angle) * errorSize + yOffset2;
    }

    if(angle >= 120) {
        visible = false;
    } else if (angle < 119) {
        visible = true;
    }

    if(visible === true) {
        drawError(x, y);
        drawCaution(x2, y2);
    } else if(visible === false && blueScreenTime < 249) {
        drawBackground();
    } else if(visible === false && (blueScreenTime > 250 || blueScreenTime < 253) ) {
        drawBlueScreen();
        blueScreenTime = 0;
    }
    angle++;
    errorSize++;
    blueScreenTime++;






};
