var x = 0;//controls position of spawner of shapes
var circleSize = 0;//controls size of spiral: will increase to spiral out
var confetti = function(x, y){
    //this is the info about the shapes spawned; they look like confetti so I called them confetti.  
    this.x = x;
    this.y = y;
    this.size = 10;
    this.color = color(random(255), random(255), random(255), random(255));
    this.speedy = random(-1, -5);
    this.speedx = random(-3, 3);
    this.rotation = 0;
    this.rotSpeed = random(1, 7);
    this.shape = floor(random(4));
};
confetti.prototype.draw = function() {
    //set style and matrix
    pushStyle();
    pushMatrix();
    rectMode(CENTER);
    translate(this.x, this.y);
    rotate(this.rotation);
    //draw shape
    noStroke();
    fill(this.color);
    switch(this.shape){
        case 0: 
            rect(0, 0, this.size, this.size);
        break;
        case 1: 
            rect(0, 0, this.size/2, this.size);
        break;
        case 2: 
            ellipse(0, 0, this.size, this.size);
        break;
        case 3: 
            ellipse(0, 0, this.size/2, this.size);
        break;
    }
    //reset style & matrix
    popStyle();
    popMatrix();
    //println(this.x+", "+this.y);
};
confetti.prototype.spin = function(){
    //ooo... spinny thing...  
    this.rotation+=this.rotSpeed;
};
confetti.prototype.fall = function(){
    //make it fall
    this.y+=this.speedy;
    //add gravity
    this.speedy+=0.2;
    //also make them spread out
    this.x+=this.speedx;
    //also make them stop spreading out
    if(this.speedx<0){
        this.speedx+=0.01;
    }else if(this.speedx>0){
        this.speedx-=0.01;
    }
};
confetti.prototype.run = function(){
    //all of them together
    this.draw();
    this.spin();
    this.fall();
};
var spawner = function(x, y, howManyFramesUntilNextSpawn){
    this.x = x;
    this.y = y;
    this.howManyFramesUntilNextSpawn = howManyFramesUntilNextSpawn;
    this.confetty = [];
};
spawner.prototype.spawn = function(){
    //add confetti (to confetty)  
    if(frameCount%this.howManyFramesUntilNextSpawn===0){
        this.confetty.push(new confetti(this.x, this.y));
    }
};
spawner.prototype.draw = function() {
    //actually draw the spawner
    strokeWeight(4);
    stroke(0);
    point(this.x, this.y);
    for(var i = 0; i<this.confetty.length; i++){
        //draw and do stuff to the confetti
        this.confetty[i].run();
        //get rid of old confetti
        if(this.confetty[i].y>405){
            this.confetty.splice(i, 1);
        }
    }
};
spawner.prototype.run = function(){
    //now we can do both at once!  
    this.spawn();
    this.draw();
};
var shapeSpawner = new spawner(200+sin(x)*circleSize, 200+sin(x-90)*circleSize, 10);
draw = function() {
    //this'll update the position
    shapeSpawner.x = 200+sin(x)*circleSize;
    shapeSpawner.y = 200+sin(x-90)*circleSize;
    //run our shape-spawner
    shapeSpawner.run();
    //make it SPIRALLY
    x++;
    circleSize+=0.05;
};
