    Country/State:AZ,USA
    What I like about programming:You can move virtual mountains. Basically, you can create a world and do whatever you like. 
**/


var iRot=0;
var iCol=[0,0,0];

var createCuboid = function(x, y, z, w, h, d) {
    var nodes = [[x,y,z],[x,y,z+d],[x,y+h,z],[x,y+h,z+d],[x+w,y,z],[x+w,y,z+d],[x+w,y+h,z],[x+w,y+h,z+d]];
    var edges = [[0,1],[1,3],[3,2],[2,0],[4,5],[5,7],[7,6],[6,4],[0,4],[1,5],[2,6],[3,7]];
    return {"nodes":nodes,"edges":edges};
};

var shape1=createCuboid(-100, -100, -100, 150, 150, 150);
var shape2=createCuboid(-50, -50, -50, 150, 150, 150);
var shapes=[shape1,shape2];

var rotateZ3D = function(theta, nodes) {
    var sinTheta = sin(theta);
    var cosTheta = cos(theta);

    for (var n = 0; n < nodes.length; n++) {
        var node = nodes[n];
        var x = node[0];
        var y = node[1];
        node[0] = x * cosTheta - y * sinTheta;
        node[1] = y * cosTheta + x * sinTheta;
    }
};

var rotateY3D = function(theta, nodes) {
    var sinTheta = sin(theta);
    var cosTheta = cos(theta);

    for (var n = 0; n < nodes.length; n++) {
        var node = nodes[n];
        var x = node[0];
        var z = node[2];
        node[0] = x * cosTheta - z * sinTheta;
        node[2] = z * cosTheta + x * sinTheta;
    }
};

var rotateX3D = function(theta, nodes) {
    var sinTheta = sin(theta);
    var cosTheta = cos(theta);

    for (var n = 0; n < nodes.length; n++) {
        var node = nodes[n];
        var y = node[1];
        var z = node[2];
        node[1] = y * cosTheta - z * sinTheta;
        node[2] = z * cosTheta + y * sinTheta;
    }
};



var draw= function() {
    var nodes, edges;

    for (var shapeNum = 0; shapeNum < shapes.length; shapeNum++) {
        nodes = shapes[shapeNum].nodes;
        edges = shapes[shapeNum].edges;

        if(shapeNum===0){
            stroke(iCol[0],iCol[1],iCol[2]);
        }else{
            stroke(255-iCol[0],255-iCol[1],255-iCol[2]);
        }

        for (var e = 0; e < edges.length; e++) {
            var n0 = edges[e][0];
            var n1 = edges[e][1];
            var node0 = nodes[n0];
            var node1 = nodes[n1];
            line(node0[0], node0[1], node1[0], node1[1]);
        }   
    }

    for (var shapeNum = 0; shapeNum < shapes.length; shapeNum++) {
        if(shapeNum===0){
            rotateX3D(random(0,iRot/100),shapes[shapeNum].nodes);
            rotateY3D(random(0,iRot/100),shapes[shapeNum].nodes);
            rotateZ3D(random(0,iRot/100),shapes[shapeNum].nodes);
        }else{
            rotateX3D(random(-iRot/100,0),shapes[shapeNum].nodes);
            rotateY3D(random(-iRot/100,0),shapes[shapeNum].nodes);
            rotateZ3D(random(-iRot/100,0),shapes[shapeNum].nodes);
        }
    }

    iCol[0]+=random(0,5);
    iCol[1]+=random(0,5);
    iCol[2]+=random(0,5);
    if(iRot>=360){
        iRot-=0.1;
    }else{
        iRot+=0.1;
    }
    for(var i=0;i<3;i++){
        if(iCol[i]>=255){
            iCol[i]=0;
        }
    }
};


translate(200, 200);
