/***
 * AUTHOR : ThinkInvisible
 * DATE   : 6/11/2015
 * PROGRAM: Branches
 * VERSION: 1.2
 * DESCRIPTION:
    Entry for the Generative Art contest.
    Draws a continuous series of branching lines across the screen and scrolls to follow an active branch.
 * ABOUT ME:
    I'm currently waiting for my second year as an electrical engineering major at a small Wisconsin college. I've always been interested in programming because a computer can do so many different things -- and something in my brain really enjoys chasing bugs around as they leave ten times as many in their wake! I've spent a lot of time writing little Javascript and C# programs just to prove a concept or to make something neat to look at for a few minutes.
 * OTHER ENTRIES:
    Crystallizer      - https://www.khanacademy.org/computer-programming/generative-art-crystallizer/4652523833131008
    Cellular Automata - https://www.khanacademy.org/computer-programming/generative-art-cellular-automata/5085736565080064
 * SOURCES:
 * CHANGELOG:
    ####### 1.0 [ 6/11/2015]
        Initial version.
    ####### 1.1 [ 6/12/2015]
        Displays a smaller version of the image in the background.
        Fixed transparency.
        Added 3D drawing mode to fix issues with fadeToMinimap.
    ####### 1.2 [ 6/12/2015]
        Implemented a fixed timestep.
        Fixed inaccuracy with 3D fadeToMinimap.
 * ISSUES:
    Deletion and focus switching code is a little touchy.
    fadeToMinimap has an unwanted exponential factor in 2D mode.
***/

//PROGRAM OPTIONS//
var targetBranches = 8; //number of live branches after which new branches will become rarer
var maxBranches = 40; //number of live branches after which no more can be created
var splitChanceLeft = 0.7; //decimal chance to create a branch to the left on death
var splitChanceRight = 0.7; //decimal chance to create a branch to the right on death
var splitChanceCenter = 0.15; //decimal chance to create a branch straight forwards on death
var splitPeriod = 750; //time between splits
var defaultSize = 30; //branch length
var defaultAngle = 45; //angle between left- and right-facing branches (turn angle is half of this)
var killOffscreen = false; //if true, dead branches that pass offscreen (including the trails of active branches!) will be deleted. --NOT SAFE FOR 3D MODE--
var killOld = splitPeriod*50; //if greater than 0, number of milliseconds before a dead branch disappears
var minimapMode = 2; //0: no minimap, 1: minimap, 2: connect minimap to normal image (active only), 3: connect minimap to normal image (all)
var minimapScale = 1/15; //how large the minimap is compared to the actual image.
var fadeToMinimap = true; //branches 'fall' towards the minimap as they become older. killOld must be greater than 0.
var timestep = 50; //amount of time the program is allowed to simulate at once.
var maxTimestep = timestep*4; //maximum amount of time the program is allowed to simulate per frame.

//3D SETUP//
var ThreeD = true; //makes use of P3D for rendering; enable if using fadeToMinimap and the exponential falloff bothers you
var canvas;
var camHeight = 100;
var camFoV = 70 * PI / 180;
var minimapDistance = -3000;
var fadeDistance = -500;
if(ThreeD) {
    canvas = createGraphics(width, height, P3D);
    canvas.angleMode = "RADIANS";
}

//SUPPORT/CLASSES//
var lastFrame = 0;
var thisFrame = 0;
var tAccum = 0;
var lFActual;
var tFActual = millis();
var scrX = 0;
var scrY = 0;
var scrXactual = 0;
var scrYactual = 0;
var focus = 0;
var branches = [];
var branch = function(foc, pos, dir, siz, ang, tim) {
    this.focus = foc;
    this.position = pos.get();
    this.endpoint = pos.get();
    this.direction = dir;
    this.size = siz;
    this.splitAng = ang;
    this.time = tim;
    this.progress = 0;
    this.dead = false;
    this.deleteFlag = false;
    this.couldntfocus=false;
    this.birthday=thisFrame;
};
var addDefaultBranch = function(parent,dirtype) {
    if(parent === undefined) {
        branches.push(new branch(
            random()*0.5+0.5,
            ThreeD?new PVector(0,0,0):new PVector(width/2,height/2),
            floor(random(0,360)/defaultAngle*2)*defaultAngle/2,
            defaultSize,
            defaultAngle,
            splitPeriod));
    } else {
        branches.push(new branch(
            random()*0.5+0.5,
            parent.endpoint.get(),
            parent.direction+(dirtype===0?-parent.splitAng/2:(dirtype===1?parent.splitAng/2:0)),
            parent.size,
            parent.splitAng,
            parent.time));
    }
};
branch.prototype.update = function(deltaT, activeBranchCount, hasFocus) {
    if(hasFocus) {scrX = this.endpoint.x-(ThreeD?0:height/2);scrY = this.endpoint.y-(ThreeD?0:width/2);}
    var abc = activeBranchCount;
    if(this.dead) {
        if(ThreeD) {
            var d = deltaT/killOld*fadeDistance;
            this.position.z += d;
            if(thisFrame - this.birthday >= this.time*2) {this.endpoint.z += d;}
        }
        if((killOffscreen && (this.endpoint.x > scrX + width || this.endpoint.x < scrX) && (this.endpoint.y > scrY + height || this.endpoint.y < scrY) &&
           (this.position.x > scrX + width || this.position.x < scrX) && (this.position.y > scrY + height || this.position.y < scrY)) ||
           (killOld > 0 && thisFrame - this.birthday > killOld)) {
            this.deleteFlag = true;
        } else if(killOld > 0 && thisFrame - this.birthday > killOld - this.time) {
            this.progress -= deltaT;
            this.position.x = this.endpoint.x + this.size * cos(this.direction+180) * this.progress/this.time;
            this.position.y = this.endpoint.y + this.size * sin(this.direction+180) * this.progress/this.time;
        }
    } else {
        this.progress += deltaT;
        this.endpoint.x = this.position.x + this.size * cos(this.direction) * this.progress/this.time;
        this.endpoint.y = this.position.y + this.size * sin(this.direction) * this.progress/this.time;
        if(this.progress >= this.time) {
            this.progress = this.time;
            abc = abc - 1;
            var fc = false;
            var firstDir = floor(random(0,2.99999999999999999999));
            var secondDir = floor(random(0,1.99999999999999999999)); if(firstDir === secondDir) {secondDir = 2;}
            var thirdDir = firstDir === 0 ? (secondDir === 1 ? 2 : 1) : (firstDir === 1 ? (secondDir === 0 ? 2 : 0) : (secondDir === 0 ? 1 : 0));
            var falloff = max(maxBranches-abc,0)/(maxBranches-targetBranches);
            if(random(0,1)<falloff*(firstDir===0?splitChanceLeft:(firstDir===1?splitChanceRight:splitChanceCenter)) && abc < maxBranches) {
                addDefaultBranch(this,firstDir);
                abc = abc + 1;
                if(this.hasFocus) {focus = branches.length-1;fc=true;}
            }
            falloff = max(maxBranches-abc,0)/(maxBranches-targetBranches);
            if(random(0,1)<falloff*(secondDir===0?splitChanceLeft:(secondDir===1?splitChanceRight:splitChanceCenter)) && abc < maxBranches) {
                addDefaultBranch(this,secondDir);
                abc = abc + 1;
                if(this.hasFocus) {focus = branches.length-1;fc=true;}
            }
            falloff = max(maxBranches-abc,0)/(maxBranches-targetBranches);
            if((random(0,1)<falloff*(thirdDir===0?splitChanceLeft:(thirdDir===1?splitChanceRight:splitChanceCenter)) && abc < maxBranches) || abc < 1) {
                addDefaultBranch(this,thirdDir);
                abc = abc + 1;
                if(this.hasFocus) {focus = branches.length-1;fc=true;}
            }
            if(this.hasFocus && !fc) {this.couldntfocus = true;}
            this.dead = true;
        }
    }
    return abc;
};
branch.prototype.draw = function() {
    stroke(255,255,255,this.focus*255);
    if(fadeToMinimap && killOld > 0) {
        var fade1 = (millis()-this.birthday+splitPeriod)/killOld;
        var fade2 = (millis()-this.birthday)/killOld;
        strokeWeight(max(((1-this.focus)*5+1)*(1-fade1),0.1));
        //line();
        line(
            lerp(this.position.x,this.position.x * minimapScale + scrXactual * (1 - minimapScale) + width/2,fade1),
            lerp(this.position.y,this.position.y * minimapScale + scrYactual * (1 - minimapScale) + height/2,fade1),
            lerp(this.endpoint.x,this.endpoint.x * minimapScale + scrXactual * (1 - minimapScale) + width/2,fade2),
            lerp(this.endpoint.y,this.endpoint.y * minimapScale + scrYactual * (1 - minimapScale) + height/2,fade2)
            );
    } else {
        strokeWeight((1-this.focus)*5+1);
        line(this.position.x,this.position.y,this.endpoint.x,this.endpoint.y);
    }
};
branch.prototype.drawmm = function() {
    stroke(125,125,125);
    strokeWeight(0.5/minimapScale);
    line(this.position.x,this.position.y,this.endpoint.x,this.endpoint.y);
};
branch.prototype.drawmmconn = function() {
    stroke(125,125,125,80);
    strokeWeight(1);
    line(this.endpoint.x,this.endpoint.y,this.endpoint.x * minimapScale + scrXactual * (1 - minimapScale) + width/2,this.endpoint.y * minimapScale + scrYactual * (1 - minimapScale) + height/2);
};
branch.prototype.draw3d = function() {
    canvas.stroke(255, 255, 255);
    canvas.noFill();
    canvas.beginShape();
    canvas.vertex(this.position.x,this.position.y,this.position.z);
    canvas.vertex(this.endpoint.x,this.endpoint.y,this.endpoint.z);
    if(minimapMode === 3 || (minimapMode === 2 && !this.dead)) {
        canvas.stroke(65,65,65);
        canvas.vertex(this.endpoint.x,this.endpoint.y,this.endpoint.z);
        canvas.vertex(this.endpoint.x,this.endpoint.y,minimapDistance);
        canvas.vertex(this.position.x,this.position.y,minimapDistance);
        canvas.endShape();
    } else if(minimapMode > 0) {
        canvas.endShape();
        canvas.stroke(65,65,65);
        canvas.beginShape();
        canvas.vertex(this.position.x,this.position.y,minimapDistance);
        canvas.vertex(this.endpoint.x,this.endpoint.y,minimapDistance);
        canvas.endShape();
    } else {canvas.endShape();}
};

//MAIN PROGRAM//
var activeCount = 0;
var populateBranches = function() {
    for(var i = 0; i < targetBranches; i++) {
        addDefaultBranch();
    }
    activeCount += targetBranches;
};
populateBranches();
background(0);
var update = function() {
    lFActual = tFActual;
    lastFrame = thisFrame;
    tFActual = millis();
    tAccum += tFActual-lFActual;
    tAccum = min(tAccum,maxTimestep);
    while(tAccum > timestep) {
        tAccum -= timestep;
        lastFrame = thisFrame;
        thisFrame += timestep;
        scrXactual = lerp(scrXactual, scrX, min(2*timestep/1000,1));
        scrYactual = lerp(scrYactual, scrY, min(2*timestep/1000,1));
        if(branches.length === 0) {populateBranches();}
        for(var i = 0; i < branches.length; i++) {
            activeCount = branches[i].update(timestep,activeCount,focus===i);
        }
        if(focus >= branches.length || branches[focus].couldntfocus || branches[focus].dead) {
        var newfocus = false;
        for(var i = 0; i < branches.length; i++) {
            if(!branches[i].dead) {newfocus=true;focus=i;break;}
        }
    }
    for(var i = branches.length-1; i >= 0; i--) {
        if(branches[i].deleteFlag) {branches.splice(i,1);if(i<focus){focus--;}i--;}
    }
    }
};

var draw = function() {
    update();

    background(0);
    if(ThreeD) {
        if(!canvas) {return;} //wait until P3D initializes, if applicable
        canvas.hint(ENABLE_DEPTH_SORT);
        canvas.beginDraw();
        canvas.background(0, 0, 0, 0);
        canvas.perspective(camFoV, width/height, 0.001, 10000);
        canvas.camera(
            //POSITION
            scrXactual,
            scrYactual,
            camHeight,
            //EYE VECTOR
            scrXactual,
            scrYactual,
            0,
            //UP VECTOR
            0,
            1,
            0
        );
        canvas.noLights();
        for(var i = 0; i < branches.length; i++) {
            branches[i].draw3d();
        }
        canvas.endDraw();
        image(canvas,0,0,width,height);
    } else {
        if(minimapMode > 0) {
            translate(width/2,height/2);
            scale(minimapScale);
            translate(-scrXactual,-scrYactual);
            for(var i = 0; i < branches.length; i++) {
                branches[i].drawmm();
            }
            /*fill(0,0,0,80);
            rect(0,0,width,height);*/
            translate(scrXactual,scrYactual);
            scale(1/minimapScale);
            translate(-width/2,-height/2);
        }
        translate(-scrXactual,-scrYactual);
        if(minimapMode > 1) {
            for(var i = 0; i < branches.length; i++) {
                if(minimapMode > 2 || !branches[i].dead) {branches[i].drawmmconn();}
            }
        }
        for(var i = 0; i < branches.length; i++) {
            branches[i].draw();
        }
        resetMatrix();
    }
    text(activeCount + " " + branches.length,0,10);
};
