/**
 * 
 * I used translation, rotation, filters, etc. to create animations, designs, and blends of color in this program. The animation will not end and goes on a loop. Enjoy!
 * 
 * 
 * 
 * I am going into 7th grade and am from New Jersey. I love being able to create anything with programming; there are no limits to what you can create.
 * 
 * I always wanted to program a game, and now after learning it here I am able to do that and more I never could have a year ago.
 * 
 * 
 * */











//Variables



//This is a timer. When it increases a certain time, the animation can start
var timer = 0;
//

//First a small point moves to the right and creates a line. This is the X variable
var pointX = 200;
//



//////////////////This is the center triangle rotation variables
var triRotate = 0;
var triRotatespeed = 6;
////////////

//The Square rotation, size, and speed variables
var squareRotate = 0;
var squareRotate2 = 0;
var squareRotate3 = 0;
var squareRotate4 = 0;
var squareSize = 50;
var squareSizespeed = 5;
//
//hectagon X position
var hectagonX = -14;
var hectagonY = 100;
var hectspeed = 7;
//
//Octagon X position
var octagonX = 328;
var octspeed = 7;
//
//hexagon Y position
var hexagonY = 100;
var hexspeed = 7;
//
//pentagon Y position
var pentagonY = 344;

var pentspeed = 7;
//


background(0, 0, 0);

draw = function() {


  pushMatrix();
  scale(width/400,height/400);
    timer++;
    rectMode(CENTER);
   noStroke();
   fill(255, 0, 0);


   //Draw the single point
   fill(255, 255, 255);
   ellipse(pointX,200,5,5);


   //

   //Increase the position of the point, stop it at a position, then add text.

   if(timer> 100){

    pointX++;
   if(pointX > 275){
    pointX = 275;  

    strokeWeight(0.01);


    fill(255, 0, 0);
    textSize(15);

   }


   }


   ///////////////////////////////////////

   /////////Turn Line into triangle/////
   if(timer > 250){


    pushMatrix();
    translate(200,200);
    rotate(triRotate);
    fill(triRotate*3/2, triRotate, 100);
    triangle(197-200,199-200,277-200,198-200,241-200,261-200);


    popMatrix();

   }

   ////////////////////////////////

   //Rotate triangle
   if(timer > 350){
    triRotate+=triRotatespeed;
    if(triRotate > 360){
     triRotatespeed = -6;   
    }
    if(triRotate < 0){
     triRotatespeed = 6;    
    }   
   }
   //

   ////////////////////Last, Create the bouncing shapes ***************///////////////////

   fill(hectagonX*2/1.1, octagonX+100/2, triRotate^2/17);
   strokeWeight(1);


   if(timer > 600){
      //hectagon
   beginShape();
   vertex(hectagonX+1,hectagonY+100);
   vertex(hectagonX+30,hectagonY+70);
   vertex(hectagonX+70,171);
   vertex(hectagonX+90,190);
   vertex(hectagonX+90,210);
   vertex(hectagonX+70,230);
   vertex(hectagonX+30,230);
   vertex(hectagonX+0,200);
   endShape();

   //Bounce code... the shape bounces up at a speed that will decrease like gravity is pulling it. After the speed reaches its value in a negative form, the speed is reset.
   hectagonX+=hectspeed;
   hectspeed-=0.5;
   if(hectspeed < -7){
    hectspeed = 7;   
   }
   // 
   }

   if(timer > 650){
    //Octagon   
    beginShape();
    vertex(octagonX,215);
    vertex(octagonX,185);
    vertex(octagonX+20,165);
    vertex(octagonX+50,165);
    vertex(octagonX+70,185);
    vertex(octagonX+70,215);
    vertex(octagonX+50,235);
    vertex(octagonX+20,235);
    endShape();

    //Bounce code... the shape bounces up at a speed that will decrease like gravity is pulling it. After the speed reaches its value in a negative form, the speed is reset.

     octagonX-=octspeed;
   octspeed-=0.5;
    if(octspeed < -7){
    octspeed = 7;   
   }

   }
   if(timer > 700){
    //Hexagon
    beginShape();
    vertex(180,hexagonY-100);
    vertex(220,hexagonY-100);
    vertex(250,hexagonY-80);
    vertex(220,hexagonY-50);
    vertex(180,hexagonY-50);
    vertex(150,hexagonY-80);
    endShape();


    //Bounce code... the shape bounces up at a speed that will decrease like gravity is pulling it. After the speed reaches its value in a negative form, the speed is reset.
     hexagonY+=hexspeed;
   hexspeed-=0.5;
    if(hexspeed < -7){
    hexspeed = 7;   
   }

   }
   if(timer > 800){
      //Pentagon
      beginShape();
      vertex(200,pentagonY);
      vertex(240,pentagonY+20);
      vertex(215,pentagonY+50);
      vertex(185,pentagonY+50);
      vertex(160,pentagonY+20);
      endShape();

      //Bounce code... the shape bounces up at a speed that will decrease like gravity is pulling it. After the speed reaches its value in a negative form, the speed is reset.
       pentagonY-=pentspeed;
   pentspeed-=0.5;
   if(pentspeed < -7){
    pentspeed = 7;   
   }

   }



   //////////////////////////////////////////////////////////////////////////////////

   //
  popMatrix();

   ////////////Next, create the spinning squares//////////////////**********************///////////////////////

   //Draw them

   if(timer > 450){
       rectMode(CORNER);
        fill(triRotate*1.5/2,triRotatespeed*20, triRotate+20/0.4);

   //Top left corner square
   pushMatrix();

   translate(71,37);
   rotate(squareRotate);
   rect(0,0,50,50);

   popMatrix();

   //Top right square

   pushMatrix();

   translate(355,37);
   rotate(squareRotate2);
   rect(0,0,50,50);

   popMatrix();


   //Bottom Left Square

   pushMatrix();

   translate(71,313);
   rotate(squareRotate3);
   rect(0,0,50,50);

   popMatrix();


   //Bottom Right square

   pushMatrix();

   translate(348,313);
   rotate(squareRotate4);
   rect(0,0,50,50);

   popMatrix();

   //Rotate squares
   squareRotate++;
   squareRotate2++;
   squareRotate3++;
   squareRotate4++;

   }


   //






   ////////////////////////////////////////////////////////////////////////////////////////////////

  //Finally, when the timer reaches certain points, filters will change the program.
  if(timer > 1200){
       filter(BLUR);

   }
  if(timer > 1500){
   filter(DILATE);

  }

  ///Restart Program
  if(timer > 1800){
   timer = 0; 
  }

  //


};
