    strokeWeight(5);
    rect(x, y, 65,79);
    rect(x- 29, y-22, 70,57);

    //this is what makes the ball go on that course it is! the next comment is where the positioning if statments end. then comes the color changing variables, hence the name!
    if(x > 390){
       Xspeed = -14;
    }


    if(x < 5){
        Xspeed = 15;    
    }

    if(y > 390){
        Yspeed = -12;
    }

    if(y < 15){
        Yspeed = 10;
    }


   //now the color changing variables! below. 



    if(color1 > 400){
        color1Change = -200;
    }

    if(color1 < 0){
        color1Change = 200;
    }

    if (color2 > 400){
    color2Change = -200;    
    }

    if(color2 < 0){
      color2Change = 200; 
    }

    if(color3 > 400){
      color3Change = -200;  
    }

    if(color3 < 0){
      color3Change = 200;  
    }


   color3 = color3 + color3Change;
    color1 = color1 + color1Change;
    color2 = color2 + color2Change;
        x = x + Xspeed;
