//setup
frameRate(30);
noStroke();
imageMode(CENTER);
background(4, 219, 37);

//all those varibles
var winston=getImage("creatures/Winston");
var noes = getImage("creatures/OhNoes");
var winstonArrayX=[40];
var winstonArrayY=[40];
var winstonArrayC=[-256];
var noesArrayX=[360];
var noesArrayY=[360];
var noesArrayC=[-4397135];
var counter=-1;
var a=0;
var winstonx=375;
var winstony=26;
var noesx=24;
var noesy=374;
var pause=false;


//Click to pause
mousePressed=function(){

    if (!pause){noLoop(); pause=true;}
    else if (pause) {loop(); pause=false;}
};

//quick way to draw Winston
var w=function (x,y,size){image(winston,x,y,size,size);};

//quick way to draw Oh noes
var n=function (x,y,size){image(noes,x,y,size,size);};

//The EPIC Appering of Winston
var wAppear=function(){

    if (counter<275){
        winstonArrayX.push(random(5,height-4));
        winstonArrayY.push(random(5,width-4));
        if (counter<200){winstonArrayC.push(-256);}
        else if (counter<250){winstonArrayC.push(-244678);}
        else if (counter<275){winstonArrayC.push(-13750744);}
    }

    if (counter<700){
        for (var i=0; i<winstonArrayX.length; i++){

            fill(winstonArrayC[i]);
            ellipse(winstonArrayX[i],winstonArrayY[i],5,5);
            if (winstonArrayX[i]<random(width-50,width-5)){winstonArrayX[i]+=1;}//Yes do not need the random, but ran out of time, oops :)
            if (winstonArrayY[i]>random(5,50)){winstonArrayY[i]-=1;}//Same here
            if (winstonArrayX[i]>width-45&&winstonArrayY[i]<45){
                if (winstonArrayX[i]<376){winstonArrayX[i]+=1;}
                if (winstonArrayX[i]>376){winstonArrayX[i]-=1;}
                if (winstonArrayY[i]>26){winstonArrayY[i]-=1;}
                if (winstonArrayY[i]<26){winstonArrayY[i]+=1;}

            }

            if (winstonArrayX[i]>375&&winstonArrayX[i]<=376&&winstonArrayY[i]>=26&&winstonArrayY[i]<27){
                a=round(random(1,2));
                if (a===1){
                    a=round(random(1,2));
                    if (a===1){winstonArrayX[i]=356;}
                    else if (a===2) {winstonArrayX[i]=396;}
                    winstonArrayY[i]=random(6,46);
                }
                else if (a===2){
                    a=round(random(1,2));
                    if (a===1){winstonArrayY[i]=6;}
                    else if (a===2){winstonArrayY[i]=46;}
                    winstonArrayX[i]=random(356,396);
                }
            }

        }

    }

    if (counter>=700&&counter<=710){fill(248, 252, 8); ellipse(376,26,100,100);}
    if (counter>710&&counter<751){w(375,26,75);}
};

//Epic Appering of Oh noes!
var nAppear=function(){

    if (counter<300){
        noesArrayX.push(random(5,height-4));
        noesArrayY.push(random(5,width-4));
        if (counter<225){noesArrayC.push(-4397135);}
        else if (counter<275){noesArrayC.push(-8317917);}
        else if (counter<290){noesArrayC.push(-13750744);}
        else if (counter<300){noesArrayC.push(-1);}
    }

    if (counter<725){
        for (var i=0; i<noesArrayX.length; i++){

            fill(noesArrayC[i]);
            ellipse(noesArrayX[i],noesArrayY[i],5,5);
            if (noesArrayX[i]>random(50,5)){noesArrayX[i]-=1;}//see commant on line 63
            if (noesArrayY[i]<random(350,395)){noesArrayY[i]+=1;}//Same here
            if (noesArrayX[i]<45&&noesArrayY[i]>355){
                if (noesArrayX[i]<24){noesArrayX[i]+=1;}
                if (noesArrayX[i]>24){noesArrayX[i]-=1;}
                if (noesArrayY[i]>374){noesArrayY[i]-=1;}
                if (noesArrayY[i]<374){noesArrayY[i]+=1;}

            }

            if (noesArrayX[i]>=23&&noesArrayX[i]<=24&&noesArrayY[i]<=375&&noesArrayY[i]>=374){
                a=round(random(1,2));
                if (a===1){
                    a=round(random(1,2));
                    if (a===1){noesArrayX[i]=44;}
                    else if (a===2) {noesArrayX[i]=4;}
                    noesArrayY[i]=random(394,354);
                }
                else if (a===2){
                    a=round(random(1,2));
                    if (a===1){noesArrayY[i]=394;}
                    else if (a===2){noesArrayY[i]=354;}
                    noesArrayX[i]=random(44,4);
                }
            }

        }

    }

    if (counter>=725&&counter<=735){fill(-4397135); ellipse(24,374,100,100);}
    if (counter>735&&counter<751){n(24,374,75);}




};

//Winston prepares
var wGetReady=function(){

    w(winstonx,winstony,75);
    if (winstonx!==350){winstonx-=1;}
    if (winstony<=317){winstony+=2;}

};

//Oh noes prepares
var nGetReady=function(){

    n(noesx,noesy,75);
    if (noesx!==69){noesx+=1;}
    if (noesy>=91){noesy-=2;}
    if (noesy===92||noesy===93){counter=1040;}
};

//Winston does his thing
var wDraw=function(){

    w(winstonx,winstony,50);
    if (counter<=1100){winstonx-=1; winstony-=5;}
    else if (counter<=1150){winstonx-=1; winstony+=5;}
    else if (counter<=1175){winstonx+=1; winstony-=5;}
    else if (counter<=1200){winstonx+=2;}
    else if (counter<=1225){winstonx-=1;}

};

//Oh noes does his thing
var nDraw=function(){

    n(noesx,noesy,50);
    if (counter<=1100){noesy+=5;}
    else if (counter<=1130){noesy-=5;}
    else if (counter<=1150){noesy-=5; noesx+=5;}
    else if (counter<=1170){noesy+=5; noesx-=5;}
    else if (counter<=1200){noesy+=5; noesx+=4.5;}
    else if (counter<=1230){noesy-=5; noesx-=4.5;}
};

//Winston ends
var wEnd=function(){

    w(winstonx,winstony,75);
};

//Oh noes ends
var nEnd=function(){

    n(noesx,noesy,75);

};

draw = function() {
    if (counter%3===0&&counter<375){background(4, 219, 37);}//Cool effects
    else if (counter>=375&&counter<1050) {background(4, 219, 37);}//to stop when ready to draw KA
    counter++;
    wAppear();
    nAppear();
    if (counter>750&&counter<1050){wGetReady(); nGetReady();}
    else if (counter>=1050&&counter<1231){wDraw(); nDraw();}
    else if (counter>=1231){wEnd(); nEnd();}
};
