var Spinner=function(x,y,speed,radius){
    this.x=x;
    this.y=y;
    //The speed it is moving at away from the generator
    this.speed=speed;
    //Half of the length of the line between the circles on each side
    this.r=radius;
    //The color of the circles
    this.color=color(random(255),random(255),random(255));
    //The color of the line
    this.stroke=color(random(255),random(255),random(255));
};
//Set it to alive
Spinner.prototype.alive=true;
Spinner.prototype.draw= function() {
    pushMatrix();
    translate(this.x,this.y);
    stroke(this.stroke);
    //Calculate the first position of the line (after translating)
    var linePos=new PVector(-sin(frameCount*3)*this.r,cos(frameCount*3)*this.r);
    //Calculate the end pos of the line by rotating the vector by 90 degrees. Don't rotate by 180 because it looks too symmetrical and the chaos is good
    var lineEndPos=linePos.get();
    lineEndPos.rotate(90);
    line(linePos.x,linePos.y,lineEndPos.x,lineEndPos.y);
    noStroke();
    fill(this.color);
    ellipse(linePos.x,linePos.y,5,5);
    fill(this.color);
    ellipse(lineEndPos.x,lineEndPos.y,5,5);
    popMatrix();
};
Spinner.prototype.update=function(){
    //Move the spinner
    this.x+=this.speed.x;
    this.y+=this.speed.y;
    //If it is off the screen it is not alive and will later be spliced from the array
    if(this.x-this.r>width||this.y-this.r>height||this.y+this.r<0||this.x+this.r<0){
        this.alive=false;
    }
};
Spinner.prototype.run=function(){
    this.draw();
    this.update();
};

var Generator=function(x,y){
    this.x=x;
    this.y=y;
    //This will hold its spinners
    this.spinners=[];
    this.color=color(random(255),random(255),random(255));
};
Generator.prototype.gen=function(){
    //Rotate by 45 degrees repeatedly to generate spinners in a circle
    for(var i=0;i<360;i+=45){
        pushMatrix();
        //Push a new spinner on the end of its spinners array with random speed
        this.spinners.push(new Spinner(55*sin(i)+this.x,55*cos(i)+this.y,new PVector(random(-4,4),random(-4,4)),30));
        popMatrix();
    }
};
Generator.prototype.draw= function() {
    //Draw it as a simple circle
    fill(this.color);
    noStroke();
    ellipse(this.x,this.y,50,50);
};
//It should be alive to begin with
Generator.prototype.alive=true;
//If you right click over it, make it not alive and it will later be spliced
Generator.prototype.remove=function(){
    if(mouseIsPressed&&mouseButton===RIGHT&&dist(mouseX,mouseY,this.x,this.y)<=50){
        this.alive=false;
    }
};
Generator.prototype.run=function(){
    this.draw();
    this.remove();
    //Every 120 frames generate a new circle of spinners
    if(frameCount%120===0){
        this.gen();
    }
    for(var i=0;i<this.spinners.length;i++){
        var spin=this.spinners[i];
        //If a spinner is off the screen remove it
        if(!spin.alive){
            this.spinners.splice(i,1);
            i--;
            continue;
        }
        this.spinners[i].run();
    }
};
//Generators array
var generators=[];
//initialize the array with 2 generators
for(var i=0;i<2;i++){
    generators.push(new Generator(random(width),random(height)));
}
background(0);
draw= function() {
    for(var i=0;i<generators.length;i++){
        generators[i].run();
        //If the generator is not alive stop it from continuing to generate
        if(!generators[i].alive){
            generators.splice(i,1);
            i--;
            continue;
        }
    }
};
