 /*

- DRAW POLLEN -

Watch it go by itself, or move your mouse into the canvas to draw yourself and watch it blow away in the wind.

Press the down arrow key to keep your drawing locked into place.

Thanks for voting!







By the way...
Make sure to try this program when putting this at the end of your URL!
?editor=no

...or just go here...

www.khanacademy.org/computer-programming/cp/6728670875746304?editor=no

Looks a lot nicer, eh?
*/



//The mouseIsClicked bool (kinda like the mouseIsPressed)
var mouseIsClicked = false;
mouseClicked = function(){
    mouseIsClicked = true;
};

//The autoDraw object
var autoDraw = {
    bool: true,
    loc: new PVector(width/2,height/2),
    speed: new PVector(1,1),
};

//store every little pollen
var pollens = [];

//Now make the pollen
var Pollen = function(loc,colour,glow){
    //Location (a PVector)
    this.loc = loc;

    //speed and speed-upness
    this.speed = new PVector(0,0);
    this.slow =  new PVector(random(0,-0.2),random(0,-0.2));

    //The nice little glow it has
    this.glow = glow||0;
    this.glowS = 0.3;

    //it's size
    this.size = 4;
    this.sizeS = random(-0.5,0.3);

    //Now to manage how long it lives
    this.life = 100;
    this.die = false;
    this.alpha = 0.8;

    //And the cool colour (yes, Canadians spell colour with a 'U')
    this.colour = colour||color(43, mouseX/2, mouseY/1.5);
};
Pollen.prototype.make = function(){

    //It's dying...
    this.life--;

    //now to make and individual matrix for each pollen
    pushMatrix();
    translate(this.loc.x,this.loc.y);

    noStroke();

    //Two crossbread colours
    fill(
        lerpColor(color(255, 255, 255,0),this.colour,this.alpha-this.alpha/2)
        );
    ellipse(0,0,this.size*1.3+this.glow,this.size*2);

    fill(
        lerpColor(color(255, 255, 255,0),this.colour,this.alpha)
        );
    ellipse(0,0,this.size,this.size);

    //You know how the glow grows and shinks? Here's where it's managed.
    this.glow+=this.glowS;

    if(this.glow>5){//To big! Shrink em'
        this.glowS=-0.3;
    }else if(this.glow<=0){//No! To small! Grow glow!
        this.glowS=0.3;
    }

    this.glowS+=-this.glowS/40;

    //Now time to kill it. Or more like start to.
    if(this.life<=0&&mouseIsPressed===false&&keyCode!==DOWN){
        this.die = true;
    }

    //time to die.
    if(this.die){
        //Makes it blow away when drawing by hand, but disapate when auto drawn
        if(autoDraw.bool===false){
            this.loc.x+=this.speed.x;
            this.loc.y+=this.speed.y;

            this.size+=this.sizeS;

            this.speed.x+=this.slow.x;
            this.speed.y+=this.slow.y;
        }

        //disapate
        this.alpha+=-0.01;
        if(this.alpha<=0.1){
            //Wah. Goodbye. No more allergies.
            pollens.splice(0,1);
        }

    }


    popMatrix();
};
Pollen.prototype.getData = function(){
    //A function that returns and object with all the info on a specific pollen

    return {
        loc:this.loc,
        glow:this.glow,
        life:this.life,
        colour:this.colour,
    };

};

//A save button (Not yet impemented)
var saveBRot = 0;
var sbRotSpeed = 6;
var saveButton = function(x,y){
    //No need to look in here. Not put in yet.

    pushMatrix();
    translate(x,y);

    fill(255, 255, 255,200);
    strokeWeight(10);
    stroke(255, 255, 255,150);
    ellipse(0,0,40,40);

    rotate(saveBRot);
    text("save",0,4);

    popMatrix();

    if(dist(mouseX,mouseY,x,y)<=40){
        saveBRot += sbRotSpeed;

        if(sbRotSpeed>0){
            sbRotSpeed += -0.4;
        }
        cursor(HAND);

    }else if(saveBRot>0){
        sbRotSpeed = 6;

        saveBRot += -sbRotSpeed;

    }


};

//Draw it yourself when the cursor goes inside canvas
mouseOver = function(){
    autoDraw.bool = false;
};
//AutoDraw on when out of canvas
mouseOut = function(){
    autoDraw.bool = true;
    keyCode = 0;
};

//When the text "locked" appears on the screen, this controlls it's alpha.
var lockAlpha = 0;
var laS = 10;

//A background outside the draw loop for experiments
background(0, 0, 0);

draw = function() {
    //Cursor to an arrow. (makes it easier if I want to impement a button later on)
    cursor(ARROW);

    //The black background
    background(0, 0, 0);

    //Make it fade away slowly. Must remove background() on above line
    //fill(0,0,0,5);
    //rect(0,0,width,height);

    //All bout' the auto draw mode
    if(autoDraw.bool){

        //Push an auto drawn pollen into pollens array
        pollens.push(new Pollen(new PVector(autoDraw.loc.x+random(-5,5),autoDraw.loc.y+random(-5,5))));

        //Animate auto draw
        autoDraw.loc.x += autoDraw.speed.x;
        autoDraw.loc.y += autoDraw.speed.y;

        autoDraw.speed.y = random(-6,6);
        autoDraw.speed.x = random(-6,6);

        mouseX=autoDraw.loc.x;
        mouseY=autoDraw.loc.y;

        //Hey, no wandering pollen. This puts it back in the centre when it goes outside the canvas.
        if(autoDraw.loc.x>width||autoDraw.loc.x<0||autoDraw.loc.y>height||autoDraw.loc.y<0){
            autoDraw.loc.x=width/2;
            autoDraw.loc.y=height/2;
        }

    }

    //Push a new pollen in when drawing by hand, but keeps it below 500 pollens on the screen
    if(mouseIsPressed&&pollens.length<500){
        pollens.push(new Pollen(new PVector(mouseX+random(-5,5),mouseY+random(-5,5))));
        pollens.push(new Pollen(new PVector(mouseX+random(-5,5),mouseY+random(-5,5))));
    }

    //Draw all the little pollens!
    for(var i = 0; i<pollens.length; i++){
        var aPollen = pollens[i];
        aPollen.make();
    }

    //The lock text
    textAlign(CENTER);
    fill(255, 255, 255,lockAlpha);

    //Only when the keyCode is DOWN it is locked.
    if(keyCode===DOWN){
        //Display that the pollens are locked down
        text("Locked",width/2,height*0.1);

        //My save button... To bad it doesn't work yet. Uncomment to see it.
        //saveButton(width-50,height-50);

        //Animate it's alpha
        lockAlpha += laS;

        if(lockAlpha>=255){
            laS = -5;
        }else if(lockAlpha<=0){
            laS = 5;
        }

        //Debugging console
        debug(pollens);
    }


    //Can't forget to remind the computer I'm not clicking anymore at the end of the draw loop. 
    mouseIsClicked = false;
};




