/**CHANGE THESE VARIABLES**/
// How tight the spiral is (1 to 10,000,000,000,000)
var tightness = 5;
// Controls the speed (12 to Infinity)
var speed = 12;
// How fast the entire figure rotates (1 to 100,000)
var spiral_speed = 1;
// Background color
var b_ground = color(255);

// How many circles the spiral contains (333 to 10,000)
var nbr_circles = 333;

var deviation = 5/8;

// PHI - The golden ratio. Check out Sal's explanation here:
// https://www.khanacademy.org/math/geometry/intro_euclid/v/the-golden-ratio
var phi = (sqrt(5) + 1) / 2 - 1;

var golden_angle = phi * TWO_PI;

// THe fill() colors are Hue-Saturation-Brightness, not the default Red-Green-Blue
// The second parameter defines the color component as a value between 0 and 1
colorMode(HSB, 1);

frameRate(speed);

draw = function() {
    background(b_ground);

    fill(0);
    noStroke();

    var lg_rad = width * 0.45;
    var lg_area = sq(lg_rad) * PI;

    var mean_area = lg_area / nbr_circles;

    var min_area = mean_area * (1 - deviation);
    var max_area = mean_area * (1 + deviation);

    var cum_area = 0;

    var fudge = 0.87;

    var cx = width/2;
    var cy = height/2;

    // Cycles through the hues (0 - 1) (used on lines 96 - 98)
    var hue_incr = frameCount * 0.0002 + 0.1;
    // Using the `spiral_speed` variable
    var angle_offset = frameCount * spiral_speed / 100;

    for (var i = 1; i <= nbr_circles; i++) {

        var angle = i * (golden_angle * tightness) + angle_offset;

        var ratio = i / nbr_circles;
        var sm_area = min_area + ratio * (max_area - min_area);

        var sm_dia = 2 * sqrt(sm_area / PI);
        var adj_sm_dia = sm_dia * fudge;

        cum_area += sm_area;

        var spiral_rad = sqrt(cum_area / PI);

        // Positioning the circle using sin() and cos()
        var x = cx + cos(angle) * spiral_rad;
        var y = cy + sin(angle) * spiral_rad;

        // Changing the hues
        var hue = hue_incr * i; // Initial hue value
        hue -= floor(hue); // Get rid of the stuff before the decimal
        fill(hue, 0.8, 1); // The color of the `ellipse`

        // After all this hard work, draw the circle!  
        ellipse(x, y, adj_sm_dia, adj_sm_dia);
    }
};

